// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "./Reputation.sol";
import "./MXNBWrapper.sol";

/**
 * @title BlockPrest LoanPool
 * @notice Gestión de préstamos grupales
 */
contract LoanPool {
    MXNBWrapper public mxnb;
    Reputation public reputation;

    struct LoanGroup {
        address[] members;
        uint256 totalAmount;
        uint256 term;
        uint256 startDate;
    }
    
    mapping(bytes32 => LoanGroup) public loanGroups;
    bytes32[] public allGroupIds;
    uint256 public groupCounter;

    event LoanCreated(bytes32 groupId, address[] members);

    constructor(address _mxnbWrapper, address _reputation) {
        mxnb = MXNBWrapper(_mxnbWrapper);
        reputation = Reputation(_reputation);
    }

    function createLoanGroup(
        address[] calldata members,
        uint256 amountPerMember,
        uint256 termInMonths
    ) external returns (bytes32 groupId) {
        uint256 totalAmount = amountPerMember * members.length;
        require(mxnb.balances(address(this)) >= totalAmount, "Insufficient funds");
        
        groupId = keccak256(abi.encode(members, block.timestamp));
        loanGroups[groupId] = LoanGroup({
            members: members,
            totalAmount: totalAmount,
            term: termInMonths,
            startDate: block.timestamp
        });
        allGroupIds.push(groupId);
        groupCounter++;
        
        for (uint i = 0; i < members.length; i++) {
            // mxnb.balances[members[i]] += amountPerMember;
            mxnb.mint(members[i], amountPerMember);
            reputation.updateScore(members[i], true); // Buen historial inicial
        }
        
        emit LoanCreated(groupId, members);
    }
    /// ✅ (Opcional) Devuelve todos los LoanGroups
    function getAllLoanGroups() external view returns (
        address[][] memory membersList,
        uint256[] memory totalAmounts,
        uint256[] memory terms,
        uint256[] memory startDates,
        bytes32[] memory ids
    ) {
        uint256 len = allGroupIds.length;

        membersList = new address[][](len);
        totalAmounts = new uint256[](len);
        terms = new uint256[](len);
        startDates = new uint256[](len);
        ids = new bytes32[](len);

        for (uint i = 0; i < len; i++) {
            bytes32 id = allGroupIds[i];
            LoanGroup storage g = loanGroups[id];

            membersList[i] = g.members;
            totalAmounts[i] = g.totalAmount;
            terms[i] = g.term;
            startDates[i] = g.startDate;
            ids[i] = id;
        }
    }
}

contracts/MXNBWrapper.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/**
 * @title BlockPrest MXNB Wrapper
 * @notice Versión local de MXNB en cada cadena
 */
contract MXNBWrapper {
    mapping(address => uint256) public balances;
    address public hyperlaneAdapter;

    event TokensMinted(address indexed user, uint256 amount);

    constructor(address _adapter) {
        hyperlaneAdapter = _adapter;
    }

    function handle(uint32, bytes32, bytes calldata message) external {
        require(msg.sender == hyperlaneAdapter, "Only Hyperlane adapter");
        (address user, uint256 amount) = abi.decode(message, (address, uint256));
        balances[user] += amount;
        emit TokensMinted(user, amount);
    }
    function mint(address user, uint256 amount) external {
        // Lógica de control de acceso, por ejemplo, solo permitir a ciertos contratos o al admin
        require(msg.sender == address(this), "Unauthorized"); 
        balances[user] += amount;
        emit TokensMinted(user, amount);
    }
}

contracts/Reputation.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/**
 * @title BlockPrest Reputation
 * @notice Sistema de puntuación crediticia
 */
contract Reputation {
    mapping(address => uint256) public creditScore;
    
    event ScoreUpdated(address user, uint256 newScore);

    function updateScore(address user, bool onTimePayment) external {
        if (onTimePayment) {
            creditScore[user] += 50;
        } else {
            creditScore[user] = creditScore[user] > 100 ? creditScore[user] - 100 : 0;
        }
        emit ScoreUpdated(user, creditScore[user]);
    }

    function getCreditLevel(address user) external view returns (string memory) {
        uint256 score = creditScore[user];
        if (score >= 800) return "AAA";
        if (score >= 600) return "AA";
        if (score >= 400) return "A";
        return "B";
    }
}

